def allCharsDifferent(chars):
    for i in range(0, len(chars)):
        for j in range(i + 1,len(chars)):
            if chars[i] == chars[j]:
                return False
    return True


signal = ""
count = 4
chars4 = []
for i in range(0, 4):
    chars4.append(signal[i])

while True:
    if allCharsDifferent(chars4):
        print(count)
        break
    chars4.pop(0)
    chars4.append(signal[count])
    count += 1
